import 'dart:convert'; import 'package:http/http.dart' as http; class ChainService{ static const _eth='https://api.etherscan.io/api'; static const _bsc='https://api.bscscan.com/api'; static Future<String> balanceETH(String address) async { final u=Uri.parse('$_eth?module=account&action=balance&address=$address&tag=latest&apikey=YourApiKeyToken'); final r=await http.get(u); if(r.statusCode==200){ final m=jsonDecode(r.body); return (m['result']??'-').toString(); } throw Exception('HTTP ${r.statusCode}'); } static Future<String> balanceBSC(String address) async { final u=Uri.parse('$_bsc?module=account&action=balance&address=$address&tag=latest&apikey=YourApiKeyToken'); final r=await http.get(u); if(r.statusCode==200){ final m=jsonDecode(r.body); return (m['result']??'-').toString(); } throw Exception('HTTP ${r.statusCode}'); } }