import 'package:flutter/material.dart';
class ModuleCard extends StatelessWidget{final IconData icon; final String title; final VoidCallback onTap; const ModuleCard({super.key, required this.icon, required this.title, required this.onTap}); @override Widget build(BuildContext c){return InkWell(onTap:onTap, child: Container(decoration: BoxDecoration(color: const Color(0xFF2A221A), borderRadius: BorderRadius.circular(16)), padding: const EdgeInsets.all(12), child: Column(mainAxisAlignment: MainAxisAlignment.center, children:[Icon(icon,size:28,color:Color(0xFFD4AF37)), const SizedBox(height:8), Text(title, maxLines:2, overflow: TextOverflow.ellipsis, textAlign: TextAlign.center)])));}}
